import React, { Component } from 'react';
import styles from './OrderSystem.module.css';

import MuiThemeProvider from '@material-ui/core/styles/MuiThemeProvider';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';

import MealCategory from './MealCategory/MealCategory';
import Restaurants from './Restaurants/Restaurants';
import Dishes from './Dishes/Dishes';
import Review from './Review/Review';

const steps = ['Step 1', 'Step 2', 'Step 3', 'Review'];

function getStepContent(step) {
    switch (step) {
        case 1:
            return <MealCategory />;  

            break;
        case 2:
            return <Restaurants />; 

            break;
        case 3:
            return <Dishes />; 

            break;
        case 4:
            return <Review />;    

            break;
        default:
            return <MealCategory />;

    }
}

class OrderSystem extends Component {
    state = {  
        step: 0,
        category: '',
        numberPeople: 0,
        restaurant: '',
        dishes: []


    }

    // Go to next step
    nextStep = () => {
        const updatedStep = {
            ...this.state.step
        };

        this.setState({
            step: updatedStep + 1
        });
    }

    // Go to previous step
    prevStep = () => {
        const updatedStep = {
            ...this.state.step
        };

        if (updatedStep > 0) {
            this.setState({
                step: updatedStep - 1
            });
        }
    }

    render () {
        const activeStep = this.state.step;


        return (
            <MuiThemeProvider>
                <AppBar position="relative">
                    <Toolbar>
                        <Typography variant="h6" color="inherit" noWrap>ZenPort</Typography>
                    </Toolbar>
                </AppBar>

                <main className={styles.content}>
                    <Paper>
                        <Stepper activeStep={activeStep}>
                        {steps.map(label => (
                            <Step key={label}>
                                <StepLabel>{label}</StepLabel>
                            </Step>
                        ))}
                        </Stepper>
                        <>
                            {getStepContent(activeStep)}
                            <div className={styles.buttonContainer}>
                                {activeStep !== 0 && (
                                <Button onClick={this.prevStep}>
                                    Previous
                                </Button>
                                )}
                                <Button
                                variant="contained"
                                color="primary"
                                onClick={this.nextStep}
                                >
                                {activeStep === steps.length - 1 ? 'Submit' : 'Next'}
                                </Button>
                            </div>
                        </>    
                    </Paper>
                </main>

            </MuiThemeProvider> 
        )

    }
}

export default OrderSystem;
