import React from 'react';

import Grid from '@material-ui/core/Grid';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Typography from '@material-ui/core/Typography';
import Select from '@material-ui/core/Select';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField'
import MenuItem from '@material-ui/core/MenuItem';

const MealCategory = (props) => {

    let numberOfPeople = []; 

    for (let i = 1; i < 11; i++) {
        numberOfPeople.push(<MenuItem value={i}>{i}</MenuItem>);
    }

    return (
        <>
            <Typography variant="h6" gutterBottom>
                Step 1
            </Typography>
            <FormControl fullWidth >
                <InputLabel htmlFor="demo-controlled-open-select">Please select a meal time</InputLabel>
                <Select
                    name="Meal"
                    value={props.meal}
                    onchange="">
                    <MenuItem value="Meal">Meal</MenuItem>   
                    <MenuItem value="Big Mac">Big Mac</MenuItem>   
                    <MenuItem value="Fillet-o-Fish">Fillet-o-Fish</MenuItem>   
                </Select>
            </FormControl> 
            <FormControl fullWidth>
                <InputLabel htmlFor="demo-controlled-open-select">Please enter the number of people</InputLabel>
                <Select
                    name="People"
                    value={props.people}
                    onchange="">
                    {numberOfPeople}
                </Select>
            </FormControl> 

        </>
    );
}

export default MealCategory;
