import React, { Component } from 'react';

import Grid from '@material-ui/core/Grid';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Typography from '@material-ui/core/Typography';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';

import OrderFlow from './../OrderFlow/OrderFlow';

import JsonData from './../../data/dishes.json'

class Restaurants extends Component {
    state = {
        dishData: JsonData.dishes,
        restaurantOptions: []
    }

    continue = (e) => {
        e.preventDefault();

        this.props.nextStep();
    } 

    componentDidMount() {
        const dishData = { ...this.state.dishData };
        let restaurants = [];
        let mealTimes = [];

        console.log(dishData);

        Object.keys(dishData).map((key) => {
            let item = dishData[key];

            let restaurant = item.restaurant;
            let availableMeal = item.availableMeals;

            Object.keys(availableMeal).map((key) => {
                if (availableMeal[key] === this.props.meal) {
                    if (!(restaurants.includes(restaurant))) {
                        restaurants.push(restaurant);
                    }
                }
            })
        });

        this.setState({
            restaurantOptions: restaurants.sort()
        });

    }

    render () {
        const restaurants = this.state.restaurantOptions;
        let restaurantOptions = [];
        restaurants.map((value,key) => {
            restaurantOptions.push(<MenuItem key={key} value={value}>{value}</MenuItem>);
        });

        return (
            <> 
                <Typography variant="h6" gutterBottom>
                    Step 2
                </Typography>
                <Grid container spacing={24}>
                    <Grid item xs={12} sm={6}>
                        <FormControl fullWidth>
                            <InputLabel htmlFor="demo-controlled-open-select">Please select a meal time</InputLabel>
                            <Select
                                value={this.props.meal}
                                onChange={this.props.handleChange}
                                inputProps={{
                                    name: 'meal'
                                }}>
                                {restaurantOptions}
                            </Select>
                        </FormControl>
                    </Grid>   
                </Grid> 
                <OrderFlow 
                    activeStep={1}
                    prevStep={this.props.prevStep}
                    continue={this.continue} /> 
            </>
        );
    }
}

export default Restaurants;
