import React, { Component } from 'react';

import Grid from '@material-ui/core/Grid';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Typography from '@material-ui/core/Typography';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import Button from '@material-ui/core/Button';
import Icon from '@material-ui/core/Icon';

import OrderFlow from './../OrderFlow/OrderFlow';

import JsonData from './../../../data/dishes.json'

class Dishes extends Component {
    state = { 
        dish: '',
        servings: '',
        dishOptions: []
    }


    handleDishes = (e) => {
        e.preventDefault();

        this.setState({
            ...this.state,
            [e.target.name]: e.target.value,
        });

        this.props.handleChange(e);
    } 

    handleAddDish = (e) => {
        e.preventDefault();


    }

    handleContinue = (e) => {
        e.preventDefault();

        this.props.nextStep();
    } 

    componentDidMount() {
        const dishData = JsonData.dishes;
        let dishes = [];

        Object.keys(dishData).map((key) => {
            let item = dishData[key];

            let dish = item.name;
            let restaurant = item.restaurant;
            let availableMeal = item.availableMeals;

            Object.keys(availableMeal).map((key) => {
                if (availableMeal[key] === this.props.meal  && restaurant === this.props.restaurant) {
                    if (!(dishes.includes(dish))) {
                        dishes.push(dish);
                    }
                }
            })
        });

        this.setState({
            dishOptions: dishes.sort()
        });

    }

    render () {
        const dishes = [...this.state.dishOptions];
        let dishOptions = [];
        dishes.map((value,key) => {
            dishOptions.push(<MenuItem key={key} value={value}>{value}</MenuItem>);
        });

        let numberOfServings = []; 

        for (let i = 1; i < 11; i++) {
            numberOfServings.push(<MenuItem key={i} value={i}>{i}</MenuItem>);
        }

        return (
            <>
                <Typography variant="h6" gutterBottom>
                    Step 3
                </Typography>
                <Grid container spacing={24} alignItems="center"> 
                    <Grid item xs={12} sm={6}>
                        <FormControl fullWidth>
                            <InputLabel htmlFor="demo-controlled-open-select">Please select a dish</InputLabel>
                            <Select
                                value={this.state.dish}
                                onChange={this.handleDishes}
                                inputProps={{
                                    name: 'dish'
                                }}>
                                {dishOptions}
                            </Select>
                        </FormControl>
                    </Grid> 
                    <Grid item xs={12} sm={6}> 
                        <FormControl fullWidth>
                            <InputLabel htmlFor="demo-controlled-open-select">Please enter no. of servings</InputLabel>
                            <Select
                                value={this.state.servings}
                                onChange={this.handleDishes}
                                inputProps={{
                                    name: 'servings'
                                }}>
                                {numberOfServings}
                            </Select>
                        </FormControl> 
                    </Grid>
                    <Grid item xs={6} sm={3}> 
                        <Button
                            variant="contained"
                            color="secondary"
                            onClick={this.handleAddDish}
                            >
                            <Icon>add_circle</Icon> Add Dish
                        </Button>
                    </Grid>   
                </Grid>
                <OrderFlow 
                    activeStep={2}
                    prevStep={this.props.prevStep}
                    continue={this.handleContinue} />  
            </>
        );
    }
}

export default Dishes;
