import React, { Component } from 'react';

import Grid from '@material-ui/core/Grid';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Typography from '@material-ui/core/Typography';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';

import OrderFlow from './../OrderFlow/OrderFlow';

import JsonData from './../../../data/dishes.json'

class Dishes extends Component {
    state = { 
        dish: '',
        servings: '',
        dishOptions: []
    }

    continue = (e) => {
        e.preventDefault();

        this.props.nextStep();
    } 

    componentDidMount() {
        const dishData = JsonData.dishes;
        let dishes = [];

        Object.keys(dishData).map((key) => {
            let item = dishData[key];

            let dish = item.name;
            let restaurant = item.restaurant;
            let availableMeal = item.availableMeals;

            Object.keys(availableMeal).map((key) => {
                if (availableMeal[key] === this.props.meal  && restaurant === this.props.restaurant) {
                    if (!(dishes.includes(dish))) {
                        dishes.push(dish);
                    }
                }
            })
        });

        this.setState({
            dishOptions: dishes.sort()
        });

    }

    render () {
        const restaurants = [...this.state.restaurantOptions];
        let restaurantOptions = [];
        restaurants.map((value,key) => {
            restaurantOptions.push(<MenuItem key={key} value={value}>{value}</MenuItem>);
        });


        let numberOfServings = []; 

        for (let i = 1; i < 11; i++) {
            numberOfServings.push(<MenuItem key={i} value={i}>{i}</MenuItem>);
        }

        return (
            <>
                <Typography variant="h6" gutterBottom>
                    Step 3
                </Typography>
                <Grid container spacing={24}>
                    <Grid item xs={12} sm={6}>
                        <FormControl fullWidth>
                            <InputLabel htmlFor="demo-controlled-open-select">Please select a meal time</InputLabel>
                            <Select
                                value={this.state.dish}
                                onChange={this.props.handleChange}
                                inputProps={{
                                    name: 'dish'
                                }}>
                                {dishOptions}
                            </Select>
                        </FormControl>
                    </Grid> 
                    <Grid item xs={12} sm={6}> 
                        <FormControl fullWidth>
                            <InputLabel htmlFor="demo-controlled-open-select">Please enter the number of people</InputLabel>
                            <Select
                                value={this.state.servings}
                                onChange={this.props.handleChange}
                                inputProps={{
                                    name: 'servings'
                                }}>
                                {numberOfServings}
                            </Select>
                        </FormControl> 
                    </Grid>   
                </Grid>
                <OrderFlow 
                    activeStep={0}
                    continue={this.continue} />  
            </>
        );
    }
}

export default Dishes;
