import React from 'react';

import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Typography from '@material-ui/core/Typography';
import Select from '@material-ui/core/Select';
import Button from '@material-ui/core/Button';
import MenuItem from '@material-ui/core/MenuItem';

const styles = theme => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
    },
    formControl: {
      margin: theme.spacing.unit,
      minWidth: 120,
    },
    selectEmpty: {
      marginTop: theme.spacing.unit * 2,
    },
  });

const MealCategory = (props) => {

    let numberOfPeople = []; 

    for (let i = 1; i < 11; i++) {
        numberOfPeople.push(<MenuItem value={i}>{i}</MenuItem>);
    }

    return (
        <>
            <Typography variant="h6" gutterBottom>
                Step 1
            </Typography>
            <FormControl className={styles.formControl}>
                <InputLabel htmlFor="demo-controlled-open-select">Please select a meal</InputLabel>
                <Select
                    name="Meal"
                    value={props.meal}
                    onchange="">
                    <MenuItem value="Meal">Meal</MenuItem>   
                    <MenuItem value="Big Mac">Big Mac</MenuItem>   
                    <MenuItem value="Fillet-o-Fish">Fillet-o-Fish</MenuItem>   
                </Select>
            </FormControl> 
            <FormControl className={styles.formControl}>
                <InputLabel htmlFor="demo-controlled-open-select">Please enter the number of people</InputLabel>
                <Select
                    name="People"
                    value={props.people}
                    onchange="">
                    {numberOfPeople}
                </Select>
            </FormControl> 

        </>
    );
}

export default MealCategory;
