import React from 'react';

class Review extends Component {
    state = {  }

    render () {
        return (
            <>
                <Typography variant="h6" gutterBottom>
                    Step 3
                </Typography>
                {displayErrorMessage}
                <Grid container spacing={24} alignItems="center"> 
                    <Grid item xs={12} sm={6}>
                        <FormControl fullWidth error={this.state.dishError}>
                            <InputLabel htmlFor="demo-controlled-open-select">Please select a dish</InputLabel>
                            <Select
                                value={this.state.dish}
                                onChange={this.handleDishes}
                                inputProps={{
                                    name: 'dish'
                                }}>
                                {dishOptions}
                            </Select>
                        </FormControl>
                    </Grid> 
                    <Grid item xs={12} sm={6}> 
                        <FormControl fullWidth error={this.state.servError}>
                            <InputLabel htmlFor="demo-controlled-open-select">Please enter no. of servings</InputLabel>
                            <Select
                                value={this.state.servings}
                                onChange={this.handleDishes}
                                inputProps={{
                                    name: 'servings'
                                }}>
                                {numberOfServings}
                            </Select>
                        </FormControl> 
                    </Grid>
                    <Grid item xs={6} sm={3}> 
                        <Button
                            variant="contained"
                            color="secondary"
                            onClick={this.handleAddDish}
                            >
                            <Icon>add_circle</Icon> Add Dish
                        </Button>
                    </Grid>
                    <Grid item xs={12} sm={6}> 
                        <Table>
                            <TableHead>
                                <TableRow>
                                    <TableCell>#</TableCell>
                                    <TableCell>Dish</TableCell>
                                    <TableCell align="right">Servings</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {orderedDishesList}
                            </TableBody>
                        </Table>
                    </Grid>


                </Grid>
                <OrderFlow 
                    activeStep={3}
                    prevStep={this.props.prevStep}
                    continue={this.handleContinue} />  
            </>
        );
    }
}

export default Review;
